import React, { useState } from 'react'
import './App.css'

import Routes from './routes'
import socket from 'socket.io-client'

function App() {
  const [ra, setRa] = useState()

  const io = socket('http://localhost:5000')

  io.on('message', data => {
    setRa(data.ra)
  })
  return (
    <div className="App">
      <Routes />


      {ra ? (
        <div className="alert">
          Aluno enviou nova mensagem
        </div>
      ) : null}
    </div>
  )
}

export default App
