import React, { useState } from 'react'
import './App.css'

import Routes from './routes'
import socket from 'socket.io-client'

function App() {
  const [ra, setRa] = useState()
  const [nome, setNome] = useState()

  const io = socket('http://localhost:5000')

  io.on('message', data => {
    console.log(data)
    setRa(data.ra)
    setNome(data.nome)
  })
  return (
    <div className="App">
      <Routes />

      {ra ? (
        <div className="alert">
          Aluno {nome} enviou nova mensagem <br/>
          <a href={`/chat/${ra}`}>Abrir chat</a>
        </div>
      ) : null}
    </div>
  )
}

export default App
